import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import { AuthContext } from "../context/AuthContext";
import PageLayout from "../components/PageLayout";

function Historial() {
  const { auth } = useContext(AuthContext);

  const [historial, setHistorial] = useState([]);
  const [loading, setLoading] = useState(true);

  const [msg, setMsg] = useState({ type: "", text: "" });

  const showMsg = (type, text) => {
    setMsg({ type, text });
    setTimeout(() => setMsg({ type: "", text: "" }), 3500);
  };

  const cargarHistorial = async () => {
    setLoading(true);
    try {
      const res = await API.get("/historial/");
      setHistorial(res.data);
    } catch {
      showMsg("warning", "No tienes permisos para ver el historial.");
      setHistorial([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    cargarHistorial();
  }, []);

  const fmtFecha = (v) => {
    if (!v) return "—";
    try {
      return new Date(v).toLocaleString();
    } catch {
      return v;
    }
  };

  const right = (
    <span className="badge text-bg-primary px-3 py-2">
      Rol: <span className="fw-semibold">{auth.rol}</span>
    </span>
  );

  return (
    <PageLayout
      title="Historial de Vacunación"
      subtitle="Registros de aplicación"
      right={right}
    >
      {msg.text && (
        <div className={`alert alert-${msg.type} py-2`} role="alert">
          {msg.text}
        </div>
      )}

      {loading ? (
        <div className="text-muted">Cargando...</div>
      ) : historial.length === 0 ? (
        <div className="alert alert-warning py-2">Sin registros.</div>
      ) : (
        <div className="table-responsive">
          <table className="table table-hover align-middle">
            <thead className="table-light">
              <tr>
                <th>ID</th>
                <th>Persona</th>
                <th>Fecha</th>
                <th>Observación</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {historial.map((h) => (
                <tr key={h.id_historial}>
                  <td>{h.id_historial}</td>
                  <td>
                    <span className="badge text-bg-primary">
                      #{h.id_persona}
                    </span>
                  </td>
                  <td>{fmtFecha(h.fecha_aplicacion)}</td>
                  <td>{h.observacion || "—"}</td>
                  <td>
                    {h.activo ? (
                      <span className="badge text-bg-success">Activo</span>
                    ) : (
                      <span className="badge text-bg-secondary">Inactivo</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </PageLayout>
  );
}

export default Historial;
