import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import Navbar from "../components/Navbar";
import { AuthContext } from "../context/AuthContext";

function Vacunas() {
  const { auth } = useContext(AuthContext);
  const [vacunas, setVacunas] = useState([]);

  const [nombre, setNombre] = useState("");
  const [descripcion, setDescripcion] = useState("");

  const canEdit = auth.rol === "admin" || auth.rol === "medico";

  const cargarVacunas = async () => {
    const res = await API.get("/vacunas/");
    setVacunas(res.data);
  };

  useEffect(() => { cargarVacunas(); }, []);

  const crearVacuna = async (e) => {
    e.preventDefault();
    await API.post("/vacunas/", { nombre, descripcion });
    setNombre("");
    setDescripcion("");
    cargarVacunas();
  };

  return (
    <div>
      <Navbar />
      <div style={{ padding: 20 }}>
        <h2>Vacunas</h2>

        {canEdit && (
          <form onSubmit={crearVacuna} style={{ marginBottom: 20 }}>
            <input placeholder="Nombre" value={nombre} onChange={e => setNombre(e.target.value)} />
            <input placeholder="DescripciÃ³n" value={descripcion} onChange={e => setDescripcion(e.target.value)} />
            <button>Guardar</button>
          </form>
        )}

        <ul>
          {vacunas.map(v => (
            <li key={v.id_vacuna}>{v.nombre} - {v.descripcion}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Vacunas;
