import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import Navbar from "../components/Navbar";
import { AuthContext } from "../context/AuthContext";

function Centros() {
  const { auth } = useContext(AuthContext);
  const canEdit = auth.rol === "admin";

  const [centros, setCentros] = useState([]);
  const [loading, setLoading] = useState(true);

  // Form
  const [nombre, setNombre] = useState("");
  const [direccion, setDireccion] = useState("");
  const [telefono, setTelefono] = useState("");

  // UI feedback
  const [msg, setMsg] = useState({ type: "", text: "" });

  const showMsg = (type, text) => {
    setMsg({ type, text });
    setTimeout(() => setMsg({ type: "", text: "" }), 3000);
  };

  const cargarCentros = async () => {
    setLoading(true);
    try {
      const res = await API.get("/centros/");
      setCentros(res.data);
    } catch (e) {
      showMsg("danger", "No se pudo cargar la lista de centros.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    cargarCentros();
  }, []);

  const limpiarForm = () => {
    setNombre("");
    setDireccion("");
    setTelefono("");
  };

  const crearCentro = async (e) => {
    e.preventDefault();

    try {
      await API.post("/centros/", {
        nombre,
        direccion: direccion || null,
        telefono: telefono || null,
      });

      showMsg("success", "Centro creado correctamente.");
      limpiarForm();
      cargarCentros();
    } catch (e) {
      showMsg("danger", "No se pudo crear el centro (revisa permisos o datos).");
    }
  };

  const eliminarCentro = async (id) => {
    const ok = window.confirm("¿Seguro que deseas eliminar este centro?");
    if (!ok) return;

    try {
      await API.delete(`/centros/${id}`);
      showMsg("success", "Centro eliminado correctamente.");
      cargarCentros();
    } catch (e) {
      showMsg("danger", "No se pudo eliminar el centro (revisa permisos).");
    }
  };

  return (
    <div>
      <Navbar />

      <div className="container py-4">
        <div className="d-flex flex-wrap justify-content-between align-items-center mb-3">
          <div>
            <h2 className="fw-bold mb-1">Centros de Salud</h2>
            <p className="text-muted mb-0">
              Gestión y consulta de centros registrados.
            </p>
          </div>

          <span className="badge text-bg-primary px-3 py-2">
            Rol: <span className="fw-semibold">{auth.rol}</span>
          </span>
        </div>

        {/* Mensajes */}
        {msg.text && (
          <div className={`alert alert-${msg.type} py-2`} role="alert">
            {msg.text}
          </div>
        )}

        {/* Formulario (solo admin) */}
        {canEdit && (
          <div className="card shadow-sm border-0 mb-4">
            <div className="card-body">
              <h5 className="fw-bold mb-3">Registrar nuevo centro</h5>

              <form onSubmit={crearCentro} className="row g-3">
                <div className="col-12 col-md-6">
                  <label className="form-label">Nombre *</label>
                  <input
                    className="form-control"
                    value={nombre}
                    onChange={(e) => setNombre(e.target.value)}
                    required
                    placeholder="Ej: Centro de Salud Tarqui"
                  />
                </div>

                <div className="col-12 col-md-6">
                  <label className="form-label">Teléfono</label>
                  <input
                    className="form-control"
                    value={telefono}
                    onChange={(e) => setTelefono(e.target.value)}
                    placeholder="Ej: 0999999999"
                  />
                </div>

                <div className="col-12">
                  <label className="form-label">Dirección</label>
                  <input
                    className="form-control"
                    value={direccion}
                    onChange={(e) => setDireccion(e.target.value)}
                    placeholder="Ej: Av. X y Calle Y"
                  />
                </div>

                <div className="col-12 d-flex gap-2">
                  <button type="submit" className="btn btn-primary">
                    Guardar
                  </button>
                  <button
                    type="button"
                    className="btn btn-outline-secondary"
                    onClick={limpiarForm}
                  >
                    Limpiar
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Tabla */}
        <div className="card shadow-sm border-0">
          <div className="card-body">
            <div className="d-flex justify-content-between align-items-center mb-3">
              <h5 className="fw-bold mb-0">Listado</h5>
              <button
                className="btn btn-sm btn-outline-primary"
                onClick={cargarCentros}
              >
                Recargar
              </button>
            </div>

            {loading ? (
              <div className="text-muted">Cargando...</div>
            ) : centros.length === 0 ? (
              <div className="alert alert-warning py-2 mb-0">
                No hay centros registrados.
              </div>
            ) : (
              <div className="table-responsive">
                <table className="table table-hover align-middle">
                  <thead className="table-light">
                    <tr>
                      <th style={{ width: 80 }}>ID</th>
                      <th>Nombre</th>
                      <th>Dirección</th>
                      <th style={{ width: 160 }}>Teléfono</th>
                      <th style={{ width: 160 }}>Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {centros.map((c) => (
                      <tr key={c.id_centro}>
                        <td className="text-muted">{c.id_centro}</td>
                        <td className="fw-semibold">{c.nombre}</td>
                        <td className="text-muted">
                          {c.direccion || (
                            <span className="fst-italic">Sin dirección</span>
                          )}
                        </td>
                        <td>
                          {c.telefono ? (
                            <span className="badge text-bg-light border">
                              {c.telefono}
                            </span>
                          ) : (
                            <span className="text-muted fst-italic">—</span>
                          )}
                        </td>
                        <td>
                          {canEdit ? (
                            <button
                              className="btn btn-sm btn-outline-danger"
                              onClick={() => eliminarCentro(c.id_centro)}
                            >
                              Eliminar
                            </button>
                          ) : (
                            <span className="text-muted fst-italic">
                              Solo lectura
                            </span>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}

            {!canEdit && (
              <small className="text-muted d-block mt-2">
                *Tu rol solo permite visualizar centros.
              </small>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Centros;
