import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import { AuthContext } from "../context/AuthContext";

function Dashboard() {
  const [personas, setPersonas] = useState([]);
  const { logout } = useContext(AuthContext);

  useEffect(() => {
    API.get("/personas/")
      .then((res) => setPersonas(res.data))
      .catch((err) => console.error("Error obteniendo personas", err));
  }, []);

  const handleLogout = () => {
    logout();
    window.location.href = "/";
  };

  return (
    <div className="app-shell">
      <aside className="sidebar">
        <div>
          <div className="sidebar-title">Vacunaci칩n</div>
          <div className="sidebar-item active">Personas</div>
          <div className="sidebar-item">Centros de salud</div>
          <div className="sidebar-item">Vacunas</div>
          <div className="sidebar-item">Historial</div>
        </div>
      </aside>

      <main className="main-content">
        <header className="main-header">
          <div>
            <h1 className="main-title">Panel principal</h1>
            <p className="main-subtitle">
              Gesti칩n b치sica de personas registradas en el sistema
            </p>
          </div>

          <button className="btn-secondary" onClick={handleLogout}>
            Cerrar sesi칩n
          </button>
        </header>

        <section className="card">
          <h2 className="card-title">Personas registradas</h2>
          {personas.length === 0 ? (
            <p style={{ fontSize: "0.9rem", color: "#6b7280" }}>
              No hay personas registradas o no se pudieron cargar los datos.
            </p>
          ) : (
            <ul className="person-list">
              {personas.map((p) => (
                <li key={p.id_persona} className="person-item">
                  <div>
                    <div className="person-name">
                      {p.nombres} {p.apellidos}
                    </div>
                    <div className="person-doc">
                      Documento: {p.numero_documento}
                    </div>
                  </div>
                  <span className="person-doc">
                    Rol: {p.rol ?? "usuario"}
                  </span>
                </li>
              ))}
            </ul>
          )}
        </section>
      </main>
    </div>
  );
}

export default Dashboard;
