import { useEffect, useState, useContext } from "react";
import Navbar from "../components/Navbar";
import API from "../api/api";
import { AuthContext } from "../context/AuthContext";
import { Link } from "react-router-dom";

function Dashboard() {
  const { auth } = useContext(AuthContext);

  const [stats, setStats] = useState({
    vacunas: 0,
    centros: 0,
    personas: 0,
    historial: 0,
  });

  const [loading, setLoading] = useState(true);

  const cargarStats = async () => {
    try {
      // Nota: usamos endpoints existentes para contar
      // Personas solo admin/medico (seg√∫n RBAC)
      const [vacunasRes, centrosRes, historialRes] = await Promise.all([
        API.get("/vacunas/"),
        API.get("/centros/"),
        API.get("/historial/"),
      ]);

      let personasCount = 0;
      try {
        const personasRes = await API.get("/personas/");
        personasCount = personasRes.data.length;
      } catch (e) {
        // Si el rol no permite /personas, no rompemos el dashboard
        personasCount = 0;
      }

      setStats({
        vacunas: vacunasRes.data.length,
        centros: centrosRes.data.length,
        personas: personasCount,
        historial: historialRes.data.length,
      });
    } catch (err) {
      console.log("Error cargando stats:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    cargarStats();
  }, []);

  const saludoPorRol = () => {
    if (auth.rol === "admin") return "Administraci√≥n del sistema";
    if (auth.rol === "medico") return "Gesti√≥n cl√≠nica y registros";
    if (auth.rol === "padre") return "Consulta y seguimiento";
    return "Bienvenido";
  };

  return (
    <div>
      <Navbar />

      <div className="container py-4">
        <div className="d-flex flex-wrap justify-content-between align-items-center mb-4">
          <div>
            <h2 className="fw-bold mb-1">Dashboard</h2>
            <p className="text-muted mb-0">{saludoPorRol()}</p>
          </div>

          <span className="badge text-bg-primary px-3 py-2">
            Rol: <span className="fw-semibold">{auth.rol}</span>
          </span>
        </div>

        {/* Cards */}
        <div className="row g-3 mb-4">
          <div className="col-12 col-md-6 col-lg-3">
            <div className="card shadow-sm border-0 h-100">
              <div className="card-body">
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <p className="text-muted mb-1">Vacunas</p>
                    <h3 className="fw-bold mb-0">
                      {loading ? "..." : stats.vacunas}
                    </h3>
                  </div>
                  <span className="badge rounded-pill text-bg-light text-primary">
                    üìå
                  </span>
                </div>
                <div className="mt-3">
                  <Link to="/vacunas" className="btn btn-sm btn-outline-primary">
                    Ver m√≥dulo
                  </Link>
                </div>
              </div>
            </div>
          </div>

          <div className="col-12 col-md-6 col-lg-3">
            <div className="card shadow-sm border-0 h-100">
              <div className="card-body">
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <p className="text-muted mb-1">Centros</p>
                    <h3 className="fw-bold mb-0">
                      {loading ? "..." : stats.centros}
                    </h3>
                  </div>
                  <span className="badge rounded-pill text-bg-light text-primary">
                    üè•
                  </span>
                </div>
                <div className="mt-3">
                  <Link to="/centros" className="btn btn-sm btn-outline-primary">
                    Ver m√≥dulo
                  </Link>
                </div>
              </div>
            </div>
          </div>

          <div className="col-12 col-md-6 col-lg-3">
            <div className="card shadow-sm border-0 h-100">
              <div className="card-body">
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <p className="text-muted mb-1">Personas</p>
                    <h3 className="fw-bold mb-0">
                      {loading ? "..." : stats.personas}
                    </h3>
                  </div>
                  <span className="badge rounded-pill text-bg-light text-primary">
                    üë§
                  </span>
                </div>
                <div className="mt-3">
                  <Link to="/personas" className="btn btn-sm btn-outline-primary">
                    Ver m√≥dulo
                  </Link>
                </div>
                {auth.rol === "padre" && (
                  <small className="text-muted d-block mt-2">
                    *Tu rol no muestra el listado completo de personas.
                  </small>
                )}
              </div>
            </div>
          </div>

          <div className="col-12 col-md-6 col-lg-3">
            <div className="card shadow-sm border-0 h-100">
              <div className="card-body">
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <p className="text-muted mb-1">Historial</p>
                    <h3 className="fw-bold mb-0">
                      {loading ? "..." : stats.historial}
                    </h3>
                  </div>
                  <span className="badge rounded-pill text-bg-light text-primary">
                    üìÑ
                  </span>
                </div>
                <div className="mt-3">
                  <Link to="/historial" className="btn btn-sm btn-outline-primary">
                    Ver m√≥dulo
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Secci√≥n informativa */}
        <div className="row g-3">
