import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import Navbar from "../components/Navbar";
import { AuthContext } from "../context/AuthContext";

function Historial() {
  const { auth } = useContext(AuthContext);
  const [historial, setHistorial] = useState([]);

  const canEdit = auth.rol === "admin" || auth.rol === "medico";

  const cargar = async () => {
    const res = await API.get("/historial/");
    setHistorial(res.data);
  };

  useEffect(() => { cargar(); }, []);

  return (
    <div>
      <Navbar />
      <div style={{ padding: 20 }}>
        <h2>Historial de Vacunación</h2>

        {!canEdit && <p>Solo visualización</p>}

        <ul>
          {historial.map(h => (
            <li key={h.id_historial}>
              Persona #{h.id_persona} — Fecha: {h.fecha_aplicacion}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Historial;
