import { useState, useContext } from "react";
import API from "../api/api";
import { AuthContext } from "../context/AuthContext";

function Login() {
  const { login } = useContext(AuthContext);
  const [numeroDocumento, setNumeroDocumento] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const form = new FormData();
      form.append("username", numeroDocumento);
      form.append("password", password);

      const res = await API.post("/auth/login", form);

      // ðŸ‘‡ ahora recibimos rol tambiÃ©n
      login(res.data.access_token, res.data.rol);
      window.location.href = "/dashboard";
    } catch (err) {
      setError("Credenciales incorrectas");
    }
  };

  return (
    <div style={{ width: 320, margin: "100px auto", padding: 20, border: "1px solid #ccc", borderRadius: 8 }}>
      <h2>Login</h2>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <form onSubmit={handleLogin}>
        <label>NÃºmero de documento</label>
        <input
          style={{ width: "100%", marginBottom: 10 }}
          value={numeroDocumento}
          onChange={(e) => setNumeroDocumento(e.target.value)}
        />

        <label>ContraseÃ±a</label>
        <input
          style={{ width: "100%", marginBottom: 10 }}
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <button style={{ width: "100%", padding: 10 }} type="submit">
          Entrar
        </button>
      </form>
    </div>
  );
}

export default Login;
