import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import Navbar from "../components/Navbar";
import { AuthContext } from "../context/AuthContext";

function Vacunas() {
  const { auth } = useContext(AuthContext);
  const [vacunas, setVacunas] = useState([]);

  const [nombre, setNombre] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [edadRecomendada, setEdadRecomendada] = useState("");
  const [dosis, setDosis] = useState("");

  const canEdit = auth.rol === "admin" || auth.rol === "medico";

  const cargarVacunas = async () => {
    const res = await API.get("/vacunas/");
    setVacunas(res.data);
  };

  useEffect(() => {
    cargarVacunas();
  }, []);

  const crearVacuna = async (e) => {
    e.preventDefault();
    await API.post("/vacunas/", {
      nombre,
      descripcion,
      edad_recomendada: edadRecomendada,
      dosis_requeridas: dosis ? parseInt(dosis) : null,
    });

    setNombre("");
    setDescripcion("");
    setEdadRecomendada("");
    setDosis("");
    cargarVacunas();
  };

  return (
    <div>
      <Navbar />

      <div style={{ padding: 20 }}>
        <h2>Tipos de Vacunas</h2>

        {canEdit && (
          <form onSubmit={crearVacuna} style={{ marginBottom: 20, padding: 15, border: "1px solid #ccc", borderRadius: 8 }}>
            <h3>Crear vacuna</h3>

            <input
              placeholder="Nombre"
              style={{ width: "100%", marginBottom: 8 }}
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
            />
            <input
              placeholder="Descripción"
              style={{ width: "100%", marginBottom: 8 }}
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
            />
            <input
              placeholder="Edad recomendada"
              style={{ width: "100%", marginBottom: 8 }}
              value={edadRecomendada}
              onChange={(e) => setEdadRecomendada(e.target.value)}
            />
            <input
              placeholder="Dosis requeridas"
              style={{ width: "100%", marginBottom: 8 }}
              value={dosis}
              onChange={(e) => setDosis(e.target.value)}
            />

            <button type="submit">Guardar</button>
          </form>
        )}

        <h3>Lista</h3>
        <ul>
          {vacunas.map((v) => (
            <li key={v.id_vacuna}>
              <b>{v.nombre}</b> — {v.descripcion || "Sin descripción"}{" "}
              {v.edad_recomendada ? `| Edad: ${v.edad_recomendada}` : ""}{" "}
              {v.dosis_requeridas ? `| Dosis: ${v.dosis_requeridas}` : ""}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Vacunas;
