import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import Navbar from "../components/Navbar";
import { AuthContext } from "../context/AuthContext";

function Personas() {
  const { auth } = useContext(AuthContext);

  const [personas, setPersonas] = useState([]);
  const [loading, setLoading] = useState(true);

  const [msg, setMsg] = useState({ type: "", text: "" });

  const showMsg = (type, text) => {
    setMsg({ type, text });
    setTimeout(() => setMsg({ type: "", text: "" }), 3500);
  };

  const badgeRol = (rol) => {
    if (rol === "admin") return "text-bg-dark";
    if (rol === "medico") return "text-bg-primary";
    if (rol === "padre") return "text-bg-success";
    return "text-bg-secondary";
  };

  const cargarPersonas = async () => {
    setLoading(true);
    setMsg({ type: "", text: "" });

    try {
      const res = await API.get("/personas/");
      setPersonas(res.data);
    } catch (e) {
      // Si es 403 (padre), mostramos mensaje
      showMsg("warning", "No tienes permisos para ver el módulo de personas.");
      setPersonas([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    cargarPersonas();
  }, []);

  const puedeVer = auth.rol === "admin" || auth.rol === "medico";
  const puedeEditar = auth.rol === "admin"; // (luego aquí ponemos crear/editar/eliminar)

  return (
    <div>
      <Navbar />

      <div className="container py-4">
        <div className="d-flex flex-wrap justify-content-between align-items-center mb-3">
          <div>
            <h2 className="fw-bold mb-1">Personas</h2>
            <p className="text-muted mb-0">
              Listado de usuarios registrados (según permisos).
            </p>
          </div>

          <div className="d-flex align-items-center gap-2">
            <span className="badge text-bg-primary px-3 py-2">
              Rol: <span className="fw-semibold">{auth.rol}</span>
            </span>
            <button
              className="btn btn-sm btn-outline-primary"
              onClick={cargarPersonas}
            >
              Recargar
            </button>
          </div>
        </div>

        {/* Mensajes */}
        {msg.text && (
          <div className={`alert alert-${msg.type} py-2`} role="alert">
            {msg.text}
          </div>
        )}

        {!puedeVer ? (
          <div className="card shadow-sm border-0">
            <div className="card-body">
              <h5 className="fw-bold mb-2">Acceso restringido</h5>
              <p className="text-muted mb-0">
                Este módulo está disponible solo para <b>admin</b> y <b>medico</b>.
              </p>
            </div>
          </div>
        ) : (
          <div className="card shadow-sm border-0">
            <div className="card-body">
              <div className="d-flex flex-wrap justify-content-between align-items-center mb-3">
                <h5 className="fw-bold mb-0">Listado</h5>

                {puedeEditar && (
                  <span className="badge text-bg-light border">
                    *Admin puede gestionar usuarios (CRUD) — lo añadimos luego
                  </span>
                )}
              </div>

              {loading ? (
                <div className="text-muted">Cargando...</div>
              ) : personas.length === 0 ? (
                <div className="alert alert-warning py-2 mb-0">
                  No hay personas para mostrar.
                </div>
              ) : (
                <div className="table-responsive">
                  <table className="table table-hover align-middle">
                    <thead className="table-light">
                      <tr>
                        <th style={{ width: 80 }}>ID</th>
                        <th>Nombres</th>
                        <th>Apellidos</th>
                        <th style={{ width: 180 }}>Documento</th>
                        <th style={{ width: 120 }}>Rol</th>
                        <th style={{ width: 140 }}>Estado</th>
                      </tr>
                    </thead>
                    <tbody>
                      {personas.map((p) => (
                        <tr key={p.id_persona}>
                          <td className="text-muted">{p.id_persona}</td>
                          <td className="fw-semibold">{p.nombres}</td>
                          <td>{p.apellidos}</td>
                          <td className="text-muted">{p.numero_documento}</td>
                          <td>
                            <span className={`badge ${badgeRol(p.rol)}`}>
                              {p.rol}
                            </span>
                          </td>
                          <td>
                            {p.activo ? (
                              <span className="badge text-bg-success">
                                Activo
                              </span>
                            ) : (
                              <span className="badge text-bg-secondary">
                                Inactivo
                              </span>
                            )}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}

              <small className="text-muted d-block mt-2">
                *La visibilidad de este módulo depende del rol (RBAC).
              </small>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Personas;
