import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import { AuthContext } from "../context/AuthContext";
import PageLayout from "../components/PageLayout";

function Personas() {
  const { auth } = useContext(AuthContext);

  const [personas, setPersonas] = useState([]);
  const [loading, setLoading] = useState(true);

  const [msg, setMsg] = useState({ type: "", text: "" });

  const showMsg = (type, text) => {
    setMsg({ type, text });
    setTimeout(() => setMsg({ type: "", text: "" }), 3500);
  };

  const badgeRol = (rol) => {
    if (rol === "admin") return "text-bg-dark";
    if (rol === "medico") return "text-bg-primary";
    if (rol === "padre") return "text-bg-success";
    return "text-bg-secondary";
  };

  const cargarPersonas = async () => {
    setLoading(true);
    try {
      const res = await API.get("/personas/");
      setPersonas(res.data);
    } catch {
      showMsg("warning", "No tienes permisos para ver personas.");
      setPersonas([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    cargarPersonas();
  }, []);

  const right = (
    <span className="badge text-bg-primary px-3 py-2">
      Rol: <span className="fw-semibold">{auth.rol}</span>
    </span>
  );

  return (
    <PageLayout title="Personas" subtitle="Usuarios registrados" right={right}>
      {msg.text && (
        <div className={`alert alert-${msg.type} py-2`} role="alert">
          {msg.text}
        </div>
      )}

      {loading ? (
        <div className="text-muted">Cargando...</div>
      ) : personas.length === 0 ? (
        <div className="alert alert-warning py-2">Sin registros.</div>
      ) : (
        <div className="table-responsive">
          <table className="table table-hover align-middle">
            <thead className="table-light">
              <tr>
                <th>ID</th>
                <th>Nombres</th>
                <th>Apellidos</th>
                <th>Documento</th>
                <th>Rol</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {personas.map((p) => (
                <tr key={p.id_persona}>
                  <td>{p.id_persona}</td>
                  <td className="fw-semibold">{p.nombres}</td>
                  <td>{p.apellidos}</td>
                  <td>{p.numero_documento}</td>
                  <td>
                    <span className={`badge ${badgeRol(p.rol)}`}>
                      {p.rol}
                    </span>
                  </td>
                  <td>
                    {p.activo ? (
                      <span className="badge text-bg-success">Activo</span>
                    ) : (
                      <span className="badge text-bg-secondary">Inactivo</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </PageLayout>
  );
}

export default Personas;
