import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import Navbar from "../components/Navbar";
import { AuthContext } from "../context/AuthContext";

function Historial() {
  const { auth } = useContext(AuthContext);

  const [historial, setHistorial] = useState([]);
  const [loading, setLoading] = useState(true);

  const [msg, setMsg] = useState({ type: "", text: "" });

  const showMsg = (type, text) => {
    setMsg({ type, text });
    setTimeout(() => setMsg({ type: "", text: "" }), 3500);
  };

  const cargarHistorial = async () => {
    setLoading(true);
    setMsg({ type: "", text: "" });

    try {
      const res = await API.get("/historial/");
      setHistorial(res.data);
    } catch (e) {
      showMsg("warning", "No tienes permisos para ver el historial de vacunación.");
      setHistorial([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    cargarHistorial();
  }, []);

  const puedeVer = auth.rol === "admin" || auth.rol === "medico";

  // Formateo simple de fecha (si viene ISO)
  const fmtFecha = (valor) => {
    if (!valor) return "—";
    try {
      const d = new Date(valor);
      if (isNaN(d.getTime())) return valor;
      return d.toLocaleString();
    } catch {
      return valor;
    }
  };

  return (
    <div>
      <Navbar />

      <div className="container py-4">
        <div className="d-flex flex-wrap justify-content-between align-items-center mb-3">
          <div>
            <h2 className="fw-bold mb-1">Historial de Vacunación</h2>
            <p className="text-muted mb-0">
              Registros de vacunación (según permisos).
            </p>
          </div>

          <div className="d-flex align-items-center gap-2">
            <span className="badge text-bg-primary px-3 py-2">
              Rol: <span className="fw-semibold">{auth.rol}</span>
            </span>
            <button
              className="btn btn-sm btn-outline-primary"
              onClick={cargarHistorial}
            >
              Recargar
            </button>
          </div>
        </div>

        {/* Mensajes */}
        {msg.text && (
          <div className={`alert alert-${msg.type} py-2`} role="alert">
            {msg.text}
          </div>
        )}

        {!puedeVer ? (
          <div className="card shadow-sm border-0">
            <div className="card-body">
              <h5 className="fw-bold mb-2">Acceso restringido</h5>
              <p className="text-muted mb-0">
                Este módulo está disponible solo para <b>admin</b> y <b>medico</b>.
              </p>
            </div>
          </div>
        ) : (
          <div className="card shadow-sm border-0">
            <div className="card-body">
              <div className="d-flex flex-wrap justify-content-between align-items-center mb-3">
                <h5 className="fw-bold mb-0">Listado</h5>
                <span className="badge text-bg-light border">
                  Total: {loading ? "..." : historial.length}
                </span>
              </div>

              {loading ? (
                <div className="text-muted">Cargando...</div>
              ) : historial.length === 0 ? (
                <div className="alert alert-warning py-2 mb-0">
                  No hay registros de historial para mostrar.
                </div>
              ) : (
                <div className="table-responsive">
                  <table className="table table-hover align-middle">
                    <thead className="table-light">
                      <tr>
                        <th style={{ width: 90 }}>ID</th>
                        <th style={{ width: 140 }}>Persona</th>
                        <th style={{ width: 200 }}>Fecha</th>
                        <th>Observación</th>
                        <th style={{ width: 120 }}>Estado</th>
                      </tr>
                    </thead>
                    <tbody>
                      {historial.map((h) => (
                        <tr key={h.id_historial}>
                          <td className="text-muted">{h.id_historial}</td>
                          <td>
                            <span className="badge text-bg-primary">
                              #{h.id_persona}
                            </span>
                          </td>
                          <td className="text-muted">{fmtFecha(h.fecha_aplicacion)}</td>
                          <td className="text-muted">
                            {h.observacion ? h.observacion : <span className="fst-italic">—</span>}
                          </td>
                          <td>
                            {h.activo ? (
                              <span className="badge text-bg-success">Activo</span>
                            ) : (
                              <span className="badge text-bg-secondary">Inactivo</span>
                            )}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}

              <small className="text-muted d-block mt-2">
                *Este listado viene desde una ruta protegida del backend.
              </small>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Historial;
