import { useEffect, useState, useContext } from "react";
import API from "../api/api";
import Navbar from "../components/Navbar";
import { AuthContext } from "../context/AuthContext";

function Vacunas() {
  const { auth } = useContext(AuthContext);
  const canEdit = auth.rol === "admin" || auth.rol === "medico";

  const [vacunas, setVacunas] = useState([]);
  const [loading, setLoading] = useState(true);

  // Form
  const [nombre, setNombre] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [edadRecomendada, setEdadRecomendada] = useState("");
  const [dosis, setDosis] = useState("");

  // UI feedback
  const [msg, setMsg] = useState({ type: "", text: "" });

  const showMsg = (type, text) => {
    setMsg({ type, text });
    setTimeout(() => setMsg({ type: "", text: "" }), 3000);
  };

  const cargarVacunas = async () => {
    setLoading(true);
    try {
      const res = await API.get("/vacunas/");
      setVacunas(res.data);
    } catch (e) {
      showMsg("danger", "No se pudo cargar la lista de vacunas.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    cargarVacunas();
  }, []);

  const limpiarForm = () => {
    setNombre("");
    setDescripcion("");
    setEdadRecomendada("");
    setDosis("");
  };

  const crearVacuna = async (e) => {
    e.preventDefault();
    try {
      await API.post("/vacunas/", {
        nombre,
        descripcion,
        edad_recomendada: edadRecomendada || null,
        dosis_requeridas: dosis ? parseInt(dosis) : null,
      });

      showMsg("success", "Vacuna creada correctamente.");
      limpiarForm();
      cargarVacunas();
    } catch (e) {
      showMsg("danger", "No se pudo crear la vacuna (revisa permisos o datos).");
    }
  };

  const eliminarVacuna = async (id) => {
    const ok = window.confirm("¿Seguro que deseas eliminar esta vacuna?");
    if (!ok) return;

    try {
      await API.delete(`/vacunas/${id}`);
      showMsg("success", "Vacuna eliminada correctamente.");
      cargarVacunas();
    } catch (e) {
      showMsg("danger", "No se pudo eliminar la vacuna (revisa permisos).");
    }
  };

  return (
    <div>
      <Navbar />

      <div className="container py-4">
        <div className="d-flex flex-wrap justify-content-between align-items-center mb-3">
          <div>
            <h2 className="fw-bold mb-1">Tipos de Vacunas</h2>
            <p className="text-muted mb-0">
              Gestión y consulta de vacunas registradas en el sistema.
            </p>
          </div>

          <span className="badge text-bg-primary px-3 py-2">
            Rol: <span className="fw-semibold">{auth.rol}</span>
          </span>
        </div>

        {/* Mensajes */}
        {msg.text && (
          <div className={`alert alert-${msg.type} py-2`} role="alert">
            {msg.text}
          </div>
        )}

        {/* Formulario (solo admin/medico) */}
        {canEdit && (
          <div className="card shadow-sm border-0 mb-4">
            <div className="card-body">
              <h5 className="fw-bold mb-3">Registrar nueva vacuna</h5>

              <form onSubmit={crearVacuna} className="row g-3">
                <div className="col-12 col-md-6">
                  <label className="form-label">Nombre *</label>
                  <input
                    className="form-control"
                    value={nombre}
                    onChange={(e) => setNombre(e.target.value)}
                    required
                    placeholder="Ej: Pentavalente"
                  />
                </div>

                <div className="col-12 col-md-6">
                  <label className="form-label">Edad recomendada</label>
                  <input
                    className="form-control"
                    value={edadRecomendada}
                    onChange={(e) => setEdadRecomendada(e.target.value)}
                    placeholder="Ej: 2 meses"
                  />
                </div>

                <div className="col-12">
                  <label className="form-label">Descripción</label>
                  <textarea
                    className="form-control"
                    rows="2"
                    value={descripcion}
                    onChange={(e) => setDescripcion(e.target.value)}
                    placeholder="Descripción breve..."
                  />
                </div>

                <div className="col-12 col-md-4">
                  <label className="form-label">Dosis requeridas</label>
                  <input
                    className="form-control"
                    value={dosis}
                    onChange={(e) => setDosis(e.target.value)}
                    placeholder="Ej: 3"
                  />
                </div>

                <div className="col-12 col-md-8 d-flex align-items-end gap-2">
                  <button type="submit" className="btn btn-primary">
                    Guardar
                  </button>
                  <button
                    type="button"
                    className="btn btn-outline-secondary"
                    onClick={limpiarForm}
                  >
                    Limpiar
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Tabla */}
        <div className="card shadow-sm border-0">
          <div className="card-body">
            <div className="d-flex justify-content-between align-items-center mb-3">
              <h5 className="fw-bold mb-0">Listado</h5>
              <button className="btn btn-sm btn-outline-primary" onClick={cargarVacunas}>
                Recargar
              </button>
            </div>

            {loading ? (
              <div className="text-muted">Cargando...</div>
            ) : vacunas.length === 0 ? (
              <div className="alert alert-warning py-2 mb-0">
                No hay vacunas registradas.
              </div>
            ) : (
              <div className="table-responsive">
                <table className="table table-hover align-middle">
                  <thead className="table-light">
                    <tr>
                      <th style={{ width: 80 }}>ID</th>
                      <th>Nombre</th>
                      <th>Descripción</th>
                      <th style={{ width: 160 }}>Edad</th>
                      <th style={{ width: 120 }}>Dosis</th>
                      <th style={{ width: 160 }}>Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {vacunas.map((v) => (
                      <tr key={v.id_vacuna}>
                        <td className="text-muted">{v.id_vacuna}</td>
                        <td className="fw-semibold">{v.nombre}</td>
                        <td className="text-muted">
                          {v.descripcion || <span className="fst-italic">Sin descripción</span>}
                        </td>
                        <td>
                          {v.edad_recomendada ? (
                            <span className="badge text-bg-light border">
                              {v.edad_recomendada}
                            </span>
                          ) : (
                            <span className="text-muted fst-italic">—</span>
                          )}
                        </td>
                        <td>
                          {v.dosis_requeridas ? (
                            <span className="badge text-bg-primary">
                              {v.dosis_requeridas}
                            </span>
                          ) : (
                            <span className="text-muted fst-italic">—</span>
                          )}
                        </td>
                        <td>
                          {canEdit ? (
                            <div className="d-flex gap-2">
                              {/* Edición la hacemos después (si quieres) */}
                              <button
                                className="btn btn-sm btn-outline-danger"
                                onClick={() => eliminarVacuna(v.id_vacuna)}
                              >
                                Eliminar
                              </button>
                            </div>
                          ) : (
                            <span className="text-muted fst-italic">Solo lectura</span>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}

            {!canEdit && (
              <small className="text-muted d-block mt-2">
                *Tu rol solo permite visualizar vacunas.
              </small>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Vacunas;
